import classes from './css/Contact.module.css'
import emailjs from '@emailjs/browser';
import React, { useRef } from 'react';

function ContactPage () {
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs
        .sendForm(
          "replace with service id",
          "replace with template id",
          form.current,
          "replace with user id"
        )
        .then(
          (result) => {
            console.log(result.text);
            console.log("message sent");
          },
          (error) => {
            console.log(error.text);
          }
        );
    };

    return (
     <React.Fragment>
        <h1>ติดต่อเรา</h1>
        <section>
          <div className='w-screen h-screen grid grid-rows-2 text-white text-4xl md:grid-cols-2'>
              <div className=' w-full h-full centered md:h-screen'>
                    <iframe style = {{width: "500px", height: "330px"}} title="FIBO" src="https://maps.google.com/maps?width=600&amp;height=400&amp;hl=en&amp;q=Institute of field robotics&amp;t=p&amp;z=14&amp;ie=UTF8&amp;iwloc=B&amp;output=embed"></iframe>
                    <p className = {classes.name}>Goose Good, Inc</p>
                    <div className = {classes.detail}>
                        <p>Institute of Fields Robotics.</p>
                        <p>126 ถนนประชาอุทิศ แขวงบางมด เขตทุ่งครุ กทม. 10140</p>
                        <p>เบอร์โทร: 08x-xxxxxxx , Fax: 0xxxxxxxxx</p>
                    </div>
              </div>

              {/* page 2 */}
              <div className='w-full h-full centered md:h-screen'>
                    <form ref={form} onSubmit={sendEmail}>
                        <label>Name</label>
                        <input type="text" name="user_name" />
                        <label>Email</label>
                        <input type="email" name="user_email" />
                        <label>Message</label>
                        <textarea name="message" />
                        <input type="submit" value="Send" />
                    </form>
              </div>
          </div>
        </section>
      </React.Fragment>

    );
}

export default ContactPage;
